import { Component, OnInit } from '@angular/core';
import {FormBuilder, FormGroup, Validators} from '@angular/forms';
import {ProductHttpService} from '../../product-http.service';
import {Router} from '@angular/router';

@Component({
  selector: 'app-supplier-add',
  templateUrl: './supplier-add.component.html',
  styleUrls: ['./supplier-add.component.css']
})
export class SupplierAddComponent implements OnInit {

  form: FormGroup;
  formSubmitted = false;

  constructor(private fb: FormBuilder, private productService: ProductHttpService,
              private route: Router) { }

  ngOnInit(): void {
    this.form = this.fb.group({
      companyName: ['', Validators.required],
      bankAccountNumber: ['', [Validators.required, Validators.minLength(10)]],
    });
  }

  add(): void {
    this.formSubmitted = true;
    if (this.form.valid) {
      this.productService.add(this.form.value).subscribe(v => this.route.navigateByUrl('/products'));
    }
  }
}

<ul *ngIf="formSubmitted">
  <li *ngIf="this.form.controls['companyName'].invalid">Company name is mandatory </li>
  <li *ngIf="this.form.controls['bankAccountNumber'].invalid">There is an error with the bank account number:
    <ul>
      <li *ngIf="this.form.controls['bankAccountNumber'].errors.required">The field is mandatory</li>
      <li *ngIf="this.form.controls['bankAccountNumber'].errors.minlength">The field is too short</li>
    </ul>
  </li>
</ul>

<form [formGroup]="form" (ngSubmit)="add()">
  <div>
    <label for="companyName">company name</label>
    <input type="text" id="companyName" formControlName="companyName">
  </div>
  <div>
    <label for="bankAccountNumber">bank account number</label>
    <input type="text" id="bankAccountNumber" formControlName="bankAccountNumber">
  </div>
  <button type="submit">add</button>
</form>
